export class GR {
    /**
     * 
     * @param canvas the canvas onbject that GR frameworks draws on
     */
    constructor(canvas:string);
    /**
     * 
     * @param a 
     * @param b 
     * @param c 
     * @param d 
     */
    setviewport(a:number,  b:number, c:number, d:number):void;
    setwindow(a:number,  b:number, c:number, d:number):void;
    /**
     * plot graph
     * @param N number of data points
     * @param x x-axis data in list array
     * @param y y-axis data in list array
     */
    polyline(N:number, x:number[], y:number[]):void;

    /**
     * set the color for the polyline
     * @param color color number
     */
    setlinecolorind(color:number):void;

    /**
     * update the plot and redraw
     */
    clearws():void;
}

